@keyframes WobbleInX {
  0% {
    transform: perspective(250px) rotateX(90deg);
    opacity: 0; }

  40% {
    transform: perspective(250px) rotateX(-10deg); }

  70% {
    transform: perspective(250px) rotateX(10deg); }

  100% {
    transform: perspective(250px) rotateX(0deg);
    opacity: 1; } }

@keyframes WobbleOutX {
  0% {
    transform: perspective(250px) rotateX(0deg);
    opacity: 1; }

  100% {
    transform: perspective(250px) rotateX(90deg);
    opacity: 0; } }

@keyframes fallInX {
  0% {
    opacity: 0;
    transform: translateY(-200px); }

  100% {
    opacity: 1;
    transform: translateY(0); } }

@keyframes fallOffX {
  0% {
    opacity: 1;
    transform: rotateZ(0); }

  100% {
    opacity: 0;
    transform: translateY(200px) rotateZ(90deg); } }

@keyframes fromAbove {
  0% {
    opacity: 0;
    transform: scale(3); }

  100% {
    opacity: 1;
    transform: scale(1); } }

@keyframes toAbove {
  0% {
    opacity: 1;
    transform: scale(1); }

  100% {
    opacity: 0;
    transform: scale(3); } }

@keyframes wobbleOutY {
  0% {
    opacity: 1;
    transform: rotateZ(0); }

  20% {
    opacity: 1;
    transform: rotateZ(140deg); }

  40% {
    opacity: 1;
    transform: rotateZ(60deg); }

  60% {
    opacity: 1;
    transform: rotateZ(110deg); }

  70% {
    opacity: 1;
    transform: rotateZ(90deg) translateX(0); }

  90% {
    opacity: 1;
    transform: rotateZ(90deg) translateX(400px); }

  100% {
    opacity: 0;
    transform: rotateZ(90deg) translateX(400px); } }

@keyframes bouncySlideIn {
  0% {
    opacity: 0;
    transform: translateX(300px); }

  70% {
    opacity: 1;
    transform: translateX(-50px); }

  100% {
    opacity: 1;
    transform: translateX(0); } }

@keyframes fallOffY {
  0% {
    opacity: 1;
    transform: translateX(0) translateY(0) rotateZ(0deg); }

  50% {
    opacity: 1;
    transform: translateX(-50%) translateY(0) rotateZ(0deg); }

  75% {
    opacity: 0.75;
    transform: translateX(-50%) translateY(0) rotateZ(-30deg); }

  100% {
    opacity: 0;
    transform: translateX(-50%) translateY(300px) rotateZ(-60deg); } }

@keyframes modalSlit {
  50% {
    transform: translateZ(-250px) rotateY(89deg);
    opacity: 0.75;
    animation-timing-function: ease-in; }

  100% {
    transform: translateZ(0) rotateY(0deg);
    opacity: 1; } }

@keyframes makeWay {
  50% {
    transform: translate3d(0, 0, -50px) rotateY(5deg); }

  100% {
    transform: translate3d(0, 0, -200px); } }

